using System.Diagnostics;
using Xunit;
using DDD.Common;

namespace DDD.Test
{
    public class HtmlHelperTest
    {
        [Fact]
        public void TestClearA()
        {
            string originStr = @"<div class='Login'>< !--登录前-- ><a target = '_blank' href = '/OqZXiaLfhvEzcqI7c58ZyE79ieaIWaOhqAV9enaGYJLngfoBOn8dQS6kIzPdZdjh.shtml' > 登录 </a>< span >|</ span ><a target = '_blank' class='free_reg' href='/YslXiaLfhvEzcqI7c58ZyE79ieaIWaOhqAV9enaGYJLngfoBOn8dQS6kIzPdZdxx.shtml'>注册</a></div>";
            string expectedStr = @"<div class='Login'>< !--登录前-- > 登录 < span >|</ span >注册</div>";
            string actualStr = HtmlHelper.RemoveA(originStr).ToString();
            Assert.Equal(expectedStr, actualStr);
        }

        [Fact]
        public void TestRemoveStyle()
        {
            string originStr = "<p data-mpa-powered-by=\"yiban.io\" style=\"padding: 0px; max - width:100 %; clear: both; min - height:1em; color:#333333;font-family:-apple-system-font, BlinkMacSystemFont, 'Helvetica Neue', 'PingFang SC', 'Hiragino Sans GB', 'Microsoft YaHei UI', 'Microsoft YaHei', Arial, sans-serif;font-size:17px;font-variant-ligatures:normal;letter-spacing:0.544px;orphans:2;white-space:normal;widows:2;text-indent:2em;line-height:2em;box-sizing:border-box !important;background-color:#FFFFFF;\">123</p>";
            originStr = System.Web.HttpUtility.HtmlDecode(originStr);
            string expectedStr = "<p data-mpa-powered-by=\"yiban.io\">123</p>";
            string actualStr = HtmlHelper.RemoveStyle(originStr);
            Assert.Equal(expectedStr, actualStr);
        }

